const initialData = {
  studentname: "Hardik Patel",
  studentId: 104,
};

const createreducer = (state = initialData, action) => {
  if (action.type === "ChangeName") {
    console.log(action);
    return {
        name : action.payload
    }
  }
};

export default createreducer;
