import { legacy_createStore as createStore, applyMiddleware } from "redux";
// const reduxstore = createStore();
// export default reduxstore;

function configureStore(initialState) {
    return createStore(
        initialState,
        applyMiddleware()
    )
}

const reduxstore = configureStore();
