import React from "react";
import { connect } from "react-redux";

const Basic = (props) => {
  console.log(props);
  return (
    <>
      <span>{props.myname}, </span>
      <span>Your Student ID : {props.studid}</span>
    </>
  );
};

const mappingstate = (state) => {
  return {
    myname: state.studentname,
    studid: state.studentId,
  };
};

const changename2 = (name2) => {
  return {
    changename: (name) => {
      name2({ type: "ChangeName", payload: name2 });
    },
    changeid: (id) => {
        name2({ type: "ChangeID", payload: id });
      },
  };
};

export default connect(mappingstate, changename2)(Basic);
