import React from "react";
import { connect } from "react-redux";
import { anothername } from "./Action/myaction";
import { windowload } from "./Action/windowload";

const Basic = (props) => {
  //console.log(props);

  const onclickgetdata = () => {
    props.changename("HD");
  };

  const onclickgetdata2 = () => {
    props.changeid("14");
  };

  return (
    <>
      <span>
        <span>{props.myname}, </span>
        <span>your Student id : {props.studid} </span>
        <button
          className="btn btn-primary"
          // onClick={() => {
          //   props.changename("HD");
          // }}

          onClick={() => {
            onclickgetdata();
            onclickgetdata2();
          }}
        >
          Get Data
        </button>
      </span>

      <div className="container">
      {
        windowload.map((todoItem) => {
  return (
    <article>
      <h4>{todoItem.title}</h4>
      <p>status: {todoItem.completed ? 'Yes' : 'No'}</p>
    </article>
  );
})
      }
      </div>
    </>
  );
};

const mappingstate = (state) => {
  return {
    myname: state.studentname,
    studid: state.studentId,
  };
};

const changename2 = (argu) => {
  return {
    changename: (name) => {
      //   argu({ type: "ChangeName", payload: name });
      argu(anothername(name));
    },
    changeid: (id) => {
      argu({ type: "ChangeID", payload: id });
    },
  };
};

export default connect(mappingstate, changename2)(Basic);
