export const windowload = () => {
  return (anyname) => {
    fetch("https://jsonplaceholder.typicode.com/todos/1")
      .then((response) => response.json())
      .then((json) => {
        anyname({
          type: "ChangeName",
          payload: json.title,
        });
      });
  };
};


export const newwindowload = () => {
    return(newname) =>{
        const apilink = async () => {
            const url = 'https://jsonplaceholder.typicode.com/todos/1';
            const fetchurl = await fetch(url).then((response) => response.json())
            .then((newjson) => {
              anyname({
                type: "ChangeName",
                payload: newjson.title,
              });
            });
        }

    }
}
